INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('10', '10', 'en_us', 'Animal masks', '<p>String together your favorite beads and design the jewelry of your dreams. Fashionable fun right at your little finger tips! This kit includes 11 different colors of beads in the shape of stars and hearts, along with strings and clasps packaged in one box for you to make your jewelry from anywhere you want.</p>', '2015-02-08 17:24:39', '2015-06-14 20:03:00', '14f5d500-9f19-4717-9af3-e8f347f3eb03');
INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('12', '12', 'en_us', 'Bug Stickers', '<p>Use these colorful foam shapes to create a scary or friendly monster. Do you like monsters with one eye? Or Maybe two, or three? That\'s up to you. Give them hair, spots, feet and a tail. Afterwards, give them a name and story! <br></p>', '2015-02-08 17:29:26', '2015-06-14 19:31:21', '2b707e90-0a56-4300-ae93-9f452e6daad1');
