INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('14', '18', 'en_us', 'Contact us', '<p>Serabeena (HK) Ltd  <br>Unit 12, 19/F, Peninsula Tower,  <br>538 Castle Peak Road  <br>Kowloon  <br>Hong Kong<br><a href=\"mailto:mailto:info@serabeena.com\">info@serabeena.com</a></p>', '2015-02-08 20:19:15', '2015-02-08 20:21:42', '2389346e-9c8b-4511-b546-7c5b88c7d53e');
INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('15', '19', 'en_us', 'Make your own Fairies', '<p>For art and craft that shines.</p><p>Serabeena\'s Shimmer Writer Glitter Glue Pens are jam-packed with iridescent glitter and luminous glue to bring sparkle and a splash of colour to any craft project.</p><p>Use them to make squiggles and dots. Great for adding a sparkle to cards and invitations! Appropriate for adult scrapbooking as well. Our non-toxic formulation is safe for children and adheres to worldwide toy safety standards (ASTM, ISO, EN-71). Washes out easily with soap and warm water.</p>', '2015-02-08 20:42:00', '2015-02-08 20:42:00', 'dd9395cf-a040-46db-a375-06fe2e4780cd');
INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('16', '20', 'en_us', 'Baby Stampers', '<p>Has your little one just started grasping and holding things? Why not add a fun activity to help them express their creativity?<br></p><p>Serabeena\'s baby stampers will add animal shapes to any paper projects while their tiny hands learn how to grasp and push down.</p>', '2015-06-14 20:18:43', '2015-06-14 20:18:43', 'b21f826d-e3a3-433d-945a-8fc762823e0d');



